package conditionalstatment;

public class OddEvenVerification {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int num = 23;
			
			
			int checknum = num%2;
			
			if(checknum != 0)
			{
				System.out.println("number is odd "+ num);
			}
			
			
			 
	                else
			{
				System.out.println("number is even "+ num);
			}
			
	}

}
